<script>
  import Header from '../../components/Header.vue'
  import BScroll from '@better-scroll/core'
  import Movable from '@better-scroll/movable'
  import MouseWheel from '@better-scroll/mouse-wheel'
  import ScrollBar from '@better-scroll/scroll-bar'
  import FileSaver from 'file-saver'
  import axios from 'axios'
  import moment from 'moment'

  BScroll.use(ScrollBar)
  BScroll.use(MouseWheel)
  BScroll.use(Movable)
  export default {
    name: 'Exhibition',
    components: {
      Header
    },
    data() {
      return {
        like: false,
        large: false,
        adImg: '',
        src: '',
        src0: require('../../assets/title/title0.png'),
        id: 0,
        src1: '',
        commend: '',
        list: [],
        showList: false,
        srcShare: require('../../assets/share.png'),
        showShare: false,
        showCover: false,
        id: 0
      }
    },
    mounted() {
      this.handleSrc()
      this.list = this.$store.state.list
      axios.get('http://127.0.0.1:5000/head').then((res) => {
        if (res.data != '') this.src1 = res.data
      })
      this.initList()
      this.id = this.$route.params.id
    },
    methods: {
      download() {
        this.adImg = require(`../../assets/exhibit/exhibit${this.id}.jpg`)
        FileSaver.saveAs(this.adImg, 'image.jpg')
      },
      initList() {
        this.showList = false
        for (let item of this.list) if (item.id == this.id) this.showList = true
      },
      scroll() {
        document.body.scrollTop = document.documentElement.scrollTop = 0
      },
      handleCommend() {
        if (this.$store.state.user.login && this.commend != '') {
          let time = `${moment().year()}/${moment().month()}/${moment().date()}`
          let data = {
            time,
            commend: this.commend,
            id: this.id
          }
          this.$store.commit('Commend', data)
          this.commend = ''
          location.reload();
        }
      },
      hanleleft() {
        let length = this.$route.params.length - 0
        this.id = (length + this.id - 1) % length
        this.src = require(`../../assets/exhibit/exhibit${this.id}.jpg`)
        this.$router.push(`/exhibition/${this.id}/${length}`)
        this.initList()
      },
      hanleleright() {
        let length = this.$route.params.length - 0
        this.id = (length + this.id + 1) % length
        this.src = require(`../../assets/exhibit/exhibit${this.id}.jpg`)
        this.$router.push(`/exhibition/${this.id}/${length}`)
        this.initList()
      },
      handleSrc() {
        this.id = this.$route.params.id
        this.src = require(`../../assets/exhibit/exhibit${this.id}.jpg`)
      },
      handleLargeClick() {
        this.large = true
        this.init()
      },
      handleSmallClick() {
        this.large = false
        this.bs.destroy()
      },
      handlereflesh() {
        window.location.reload()
      },
      handledownload() {
        this.adImg = require(`../../assets/exhibit/exhibit${this.id}.jpg`)
        FileSaver.saveAs(this.adImg, 'image.jpg')
      },
      share() {
        const width = document.documentElement.clientWidth || document.body.clientWidth
        const radio = (width / 1280) * 355
        let bottom = document.documentElement.scrollHeight
        this.$refs.cover.style.height = bottom + 'px'
        this.showCover = true
        this.src = require(`../../assets/exhibit/exhibit${this.id}.jpg`)
      },
      handleshowShare() {
        this.showShare = true
        let bottom = document.documentElement.scrollHeight
        this.$refs.shareout.style.height = bottom + 'px'
      },
      init() {
        this.$nextTick(function () {
          this.bs = new BScroll(this.$refs.scroll, {
            scrollbar: true,
            bindToTarget: true,
            scrollX: true,
            scrollY: true,
            freeScroll: true,
            bounce: true,
            movable: true,
            mouseWheel: true,
            click: true
          })
        })
      }
    }
  }
</script>
